jQuery(document).ready(function($){$('.edit-tags-php.taxonomy-asd_map_category').find('table.wp-list-table').find('span.view a[href*="/map_category/general/"]').parent().parent().find('span.delete').hide();var a=$('#acf-asd_feature_place_icon');a.append('<div id="asd-icon-preview"></div>');var b=$('#acf-field-asd_feature_place_icon').val();if(b=='custom'){b=$('#acf-asd_feature_map_upload_icon').find('img.acf-image-image').attr('src');$('#asd-icon-preview').html('<img src="'+b+'" alt="map icon" />')}else{$('#asd-icon-preview').html('<img src="'+mapPath+'/images/icons/'+b+'.png" alt="map icon" />')}var c=$('#asd_map_categorychecklist');if(c.length){var d=c.find(':checkbox:checked').length>0;if(!d){c.find('label').each(function(){if($(this).text()==' General'){$(this).find('input').prop("checked",true)}})}}});jQuery('#acf-field-asd_feature_place_icon').change(function(){var a=jQuery(this).val();if(a=='custom'){a=jQuery('#acf-asd_feature_map_upload_icon').find('img.acf-image-image').attr('src');if(a){jQuery('#asd-icon-preview').html('<img src="'+a+'" alt="map icon" />')}else{jQuery('#asd-icon-preview').html('<img src="'+mapPath+'/images/icons/custom.png" alt="map icon" />')}}else{jQuery('#asd-icon-preview').html('<img src="'+mapPath+'/images/icons/'+a+'.png" alt="map icon" />')}});