jQuery(document).ready(function($){$("#asd-map-wrap").tabs();$('#asd-map-place ').spin('map','#444')});var maplace=new Maplace({map_div:'#asd-map-place',controls_div:'#asd-map-controls',controls_type:'list',controls_on_map:false,view_all:true,view_all_text:'All Places'});jQuery('#map-tabs').find('a').click(function(e){e.preventDefault();var a=jQuery(this).attr('data-load');jQuery(this).spin('map_tab','#fff');jQuery(this).parent().toggleClass('loading');showGroup(a)});function showGroup(c){var d=jQuery('#'+c);jQuery('#maps-tabs').find('li').removeClass('active');jQuery(d).parent().addClass('active');jQuery.post(MapAjax.asd_feature_map_ajaxurl,{cat:c,asd_feature_map_nonce:MapAjax.asd_feature_map_nonce,action:'asd_feature_map_action'},function(a){var b=jQuery('#map-tabs').find('li.loading');jQuery('#asd-map-place ').spin(false);b.find('a').spin(false);b.toggleClass('loading');maplace.Load({locations:a.locations,view_all_text:a.title,type:a.type,force_generate_controls:true})},'json')}